// @Units: rad/s
// @User: Advanced
// @Calibration: 1

INS_GYROFFS_Y.__field_text:
// @DisplayName: Gyro offsets of Y axis
// @Description: Gyro sensor offsets of Y axis. This is setup on each boot during gyro calibrations
